static::$smarty['author'] = array(
    'v'=>false,
    'a'=>false,
    'e'=>false,
    'd'=>false
);

$username = Auth::get_screen_name();

//使用者權限狀態控制
switch($username){
    case 'guest':
        //尚未登入
        static::$smarty['author']['v'] = true;
        break;

    default:
        //已登入的訪客
        static::$smarty['author']['v'] = true;
        static::$smarty['author']['a'] = true;
        static::$smarty['author']['e'] = true;

public function getMicrotime()
{
